pragma solidity 0.8.17;

import "./IToken.sol";

interface ITGE {
    struct TGEInfo {
        uint256 price;
        uint256 hardcap;
        uint256 softcap;
        uint256 minPurchase;
        uint256 maxPurchase;
        uint256 vestingPercent;
        uint256 vestingDuration;
        uint256 vestingTVL;
        uint256 duration;
        address[] userWhitelist;
        address unitOfAccount;
        uint256 lockupDuration;
        uint256 lockupTVL;
    }

    function initialize(
        IToken token_,
        TGEInfo calldata info,
        uint256 protocolFee
    ) external;

    enum State {
        Active,
        Failed,
        Successful
    }

    function state() external view returns (State);

    function transferUnlocked() external view returns (bool);

    function totalVested() external view returns (uint256);

    function purchaseOf(address user) external view returns (uint256);

    function vestedBalanceOf(address user) external view returns (uint256);

    function lockedBalanceOf(address account) external view returns (uint256);
}
